css:
.print-format {
 margin-left: 0mm;
 margin-top: 0mm;
 margin-bottom: 0mm;
 margin-right: 0mm;
}

@font-face {
    font-family: 'MetaOT';
    font-style: normal;
    font-weight: normal;
    src: url('/assets/mvd/font/MetaOT-Normal.woff');
}

#logo_kopfzeile {
    width: 40mm;
    margin-left: 15mm;
    padding-top: 14.5mm !important;
}

#anschrift {
    max-width: 80mm;
    width: 80mm;
    margin-left: 30mm;
    float: left;
    font-size: 10pt;
    line-height: 13pt;
    font-family: 'MetaOT';
    margin-top: 26mm;
}

#ansprechperson_1 {
    float: left;
    margin-top: 26mm;
    margin-left: 10mm;
    width: 50mm;
    max-width: 50mm;
    font-size: 8pt;
    line-height: 10pt;
    color: gray;
    font-family: 'MetaOT';
}

#inhalt {
    clear: left;
    margin-top: 69.25mm;
    margin-left: 30mm;
    height: 168.75mm;
    max-height: 168.75mm;
    font-size: 10pt;
    line-height: 13pt;
    width: 154mm;
    max-width: 154mm;
    font-family: 'MetaOT';
}
#fusszeile_1 {
    margin-left: 30mm;
    height: 17mm;
    max-height: 17mm;
    float: left;
    width: 61mm;
    max-width: 61mm;
    font-size: 8pt;
    line-height: 10pt;
    color: #0063ae;
    font-weight: bold;
    font-family: 'MetaOT';
}

#fusszeile_2 {
    height: 17mm;
    max-height: 17mm;
    float: left;
    width: 41mm;
    max-width: 41mm;
    font-size: 8pt;
    line-height: 10pt;
    color: #0063ae;
    font-family: 'MetaOT';
}
#fusszeile_3 {
    height: 17mm;
    max-height: 17mm;
    float: left;
    font-size: 8pt;
    line-height: 10pt;
    color: #0063ae;
    font-family: 'MetaOT';
}

#inhaltrueckseite {
    margin-left: 30mm;
    padding-top: 52.5mm;
    width: 154mm;
    max-width: 154mm;
    font-family: 'MetaOT';
    height: 268.75mm;
    max-height: 268.75mm;
}

html:
{% set sektion = frappe.get_doc("Sektion", doc.sektion_id) %}
{% set mitgliedschaft = doc %}
<div id="logo_kopfzeile"><img src="assets/mvd/img/MV_Logo_F.png"></div>
<div id="anschrift">
    {{ doc.adressblock | replace("\n", "<br>") }}
</div>
{% if not doc.check_ansprechperson %}
<div id="ansprechperson_1">
        {{ sektion.default_ort }}, {{ frappe.utils.get_datetime(frappe.utils.now()).strftime('%d.%m.%Y') }}<br>
        Mitgl.-Nr: {{ doc.mitglied_nr }}
</div>
{% endif %}
{% set textkonserven = frappe.get_all('Textkonserven', filters={'sektion_id': sektion.name, 'rechnungstyp': 'Kündigung'}, fields=['textkonserve', 'rueckseite', 'textkonserve_rueckseite', 'fusszeile_ausblenden']) %}
{% if textkonserven|length > 0 %}
    {% set textkonserve = textkonserven[0] %}
{% else %}
    {% set textkonserve = {'textkonserve': '<h1>Keine Textkonserve gefunden!</h1>'} %}
{% endif %}
<div id="inhalt">
    <strong>Kündigung Mitgliedschaft {{ doc.mitglied_nr }} per {{ frappe.utils.get_datetime(doc.kuendigung).strftime('%d.%m.%Y') }}</strong><br><br><br>
    {{ textkonserve.textkonserve|replace("%%ANREDE%%", mitgliedschaft.briefanrede) }}
</div>
{% if not textkonserve.fusszeile_ausblenden %}
<div id="fusszeile_1">
    {{ sektion.fz_links }}
</div>
<div id="fusszeile_2">
    {{ sektion.fz_mitte }}
</div>
<div id="fusszeile_3">
    {{ sektion.fz_rechts }}
</div>
{% else %}
<div id="fusszeile_1">
    &nbsp;
</div>
<div id="fusszeile_2">
    &nbsp;
</div>
<div id="fusszeile_3">
    &nbsp;
</div>
{% endif %}
<!-- optionale 2. seite -->
{% if textkonserve.rueckseite %}
<div class="page-break"></div>
<div id="inhaltrueckseite">
    {{ textkonserve.textkonserve_rueckseite|replace("%%ANREDE%%", mitgliedschaft.briefanrede) }}
</div>
{% endif %}
{% if not textkonserve.fusszeile_ausblenden %}
<div id="fusszeile_1">
    {{ sektion.fz_links }}
</div>
<div id="fusszeile_2">
    {{ sektion.fz_mitte }}
</div>
<div id="fusszeile_3">
    {{ sektion.fz_rechts }}
</div>
{% else %}
<div id="fusszeile_1">
    &nbsp;
</div>
<div id="fusszeile_2">
    &nbsp;
</div>
<div id="fusszeile_3">
    &nbsp;
</div>
{% endif %}
