html:
{% set sektion = frappe.get_doc("Sektion", doc.sektion_id) %}
{% set mitgliedschaft = frappe.get_doc("MV Mitgliedschaft", doc.mv_mitgliedschaft) %}
{% if doc.check_ansprechperson and doc.mit_ausweis %}
<div id="logo_kopfzeile" style="float: left;"><img src="assets/mvd/img/MV_Logo_F.png"></div>
<div id="ausweis_1">
    <div id="ausweis_titel">MITGLIEDERAUSWEIS 2021</div>
    <div id="logo_ausweise"><img src="assets/mvd/img/MV_Logo_F.png"></div>
    <div id="text_ausweis">
        {% if mitgliedschaft.kundentyp == 'Unternehmen' %}
            {{ mitgliedschaft.firma }}<br>
        {% endif %}
        {{ mitgliedschaft.vorname_1 }}  {{ mitgliedschaft.nachname_1 }}<br>
        {% if mitgliedschaft.hat_solidarmitglied %}
            {{ mitgliedschaft.vorname_2 }} {{ mitgliedschaft.nachname_2 }}<br>
        {% endif %}
        {% if mitgliedschaft.kundentyp != 'Unternehmen' %}<br>{% endif %}
    </div>
    <div id="text_ausweis_sektion">SEKTION</div>
    <div id="wert_ausweis_sektion">{{ doc.sektion_id }}</div>
    <div id="text_ausweis_mitgliedernummer">MITGLIEDERNUMMER</div>
    <div id="wert_ausweis_mitgliedernummer">01234567</div>
</div>
<div style="clear: both;"></div>
<div id="anschrift" style="margin-top: 0px !important;">
    Musterfirma AG<br>
    Maire Mustermensch<br>
    Hans-Mustermensch-Strasse 155b<br>
    8005 Zürich
</div>
{% else %}
<div id="logo_kopfzeile"><img src="assets/mvd/img/MV_Logo_F.png"></div>
<div id="anschrift">
    Musterfirma AG<br>
    Maire Mustermensch<br>
    Hans-Mustermensch-Strasse 155b<br>
    8005 Zürich
</div>
{% endif %}
{% if not doc.check_ansprechperson %}
<div id="ansprechperson_1">
        Ansprechperson<br>
        E-Mail<br>
        Direktwahl
</div>
<div id="ansprechperson_2">
        {{ doc.ansprechperson }}<br>
        {{ doc.email_ma }}<br>
        {{ doc.tel_ma }}
</div>
{% endif %}

<div id="inhalt"{% if doc.mit_ausweis %} style="margin-top: 43.25mm !important;"{% endif %}>
    {{ doc.ort }}, {{ frappe.utils.get_datetime(doc.datum).strftime('%d.%m.%Y') }}<br><br><br>
    <b>{{ doc.brieftitel }}</b><br><br>
    {% if not doc.check_anrede %}
        {{ get_anredekonvention(doc.mv_mitgliedschaft) }}<br>
    {% else %}
        {{ doc.anrede }}<br>
    {% endif %}
    {{ doc.inhalt }}
</div>
<div id="fusszeile_1">
    {{ sektion.fz_links }}
</div>
<div id="fusszeile_2">
    {{ sektion.fz_mitte }}
</div>
<div id="fusszeile_3">
    {{ sektion.fz_rechts }}
</div>
{% if doc.inhalt_2 and doc.inhalt_2 != '<div><br></div>' %}
    <div class="page-break"></div>
    {% if doc.check_ansprechperson and doc.mit_ausweis %}
        <div id="ausweis_2">
            {{ sektion.ausweis_rueckseite }}
        </div>
        <div id="seite_2">{{ doc.inhalt_2 }}</div>
    {% else %}
    <div id="seite_2" style="">{{ doc.inhalt_2 }}</div>
    {% endif %}
{% else %}
    {% if doc.check_ansprechperson and doc.mit_ausweis %}
        <div class="page-break"></div>
        <div id="ausweis_2">
            {{ sektion.ausweis_rueckseite }}
        </div>
    {% endif %}
{% endif %}
