css:
.print-format {
 margin-left: 0mm;
 margin-right: 0mm;
 margin-bottom: 0mm !important;
}

#kopfplatzhalter {
    min-height: 52.5mm;
}

#allinone {
    margin-left: 30mm;
    margin-right: 20mm;
}

#anschrift {
    height: 42mm;
    width: 90mm;
    float: left;
}

#ansprechperson_1 {
    width: 30mm;
    float: left;
}

#ansprechperson_2 {
    float: left;
}

#datumszeile {
    margin-bottom: 15mm;
    clear: both;
}

#brieftitel {
    margin-bottom: 10mm;
}

html:
{% if doc.druck_kopf_fusszeile %}
    <!-- HEAD -->
    <div  id="header-html" class="hidden-pdf" >
        {% set letter_head = frappe.get_doc("Letter Head", doc.kopf_fusszeile) %}
        {% if letter_head %}
          {{ letter_head.content }}
        {% else %}
          <p>Letter head not found. Please define the letter head under print settings.</p>
        {% endif %}
    </div>
{% else %}
    <div id="kopfplatzhalter"></div>
{% endif %}

<div id="allinone">
    <!-- load mitgliedschaft -->
    {% set mitgliedschaft = frappe.get_doc("MV Mitgliedschaft", doc.mv_mitgliedschaft) %}

    <div id="anschrift">
        {{ mitgliedschaft.vorname_1 }} {{ mitgliedschaft.nachname_1 }}<br>
        {% if mitgliedschaft.zusatz_adresse %}
            {{ mitgliedschaft.zusatz_adresse }}<br>
        {% endif %}
        {{ mitgliedschaft.strasse }} {{ mitgliedschaft.nummer }}<br>
        {{ mitgliedschaft.plz }} {{ mitgliedschaft.ort }}
    </div>
    {% if not doc.check_ansprechperson %}
        <div id="ansprechperson_1">
            {{ _("Ansprechperson") }}<br>
            {{ _("E-Mail") }}<br>
            {{ _("Direktwahl") }}
        </div>
        <div id="ansprechperson_2">
            {{ doc.ansprechperson }}<br>
            {{ doc.email_ma }}<br>
            {{ doc.tel_ma }}
        </div>
    {% endif %}
    <div id="datumszeile">{{ _(doc.ort) }}, {{ frappe.utils.get_datetime(doc.datum).strftime('%d. ') }}{{ _(frappe.utils.get_datetime(doc.datum).strftime('%B'), "de") }}&nbsp{{ frappe.utils.get_datetime(doc.datum).strftime('%Y') }}</div>
    <div id="brieftitel"><b>{{ _(doc.brieftitel) }}</b></div>
    <div id="inhalt">
        <p>{{ _(doc.anrede) }} {{ mitgliedschaft.nachname_1 }}<br><br></p>
        <p>{{ doc.inhalt }}</p>
    </div>
</div>

{% if doc.druck_kopf_fusszeile %}
<!-- Footer -->
<div id="footer-html" class="visible-pdf">
   {% if letter_head %}
    {{ letter_head.footer }}
   {% else %}
     <p>No letter head found. Please define a letter head under print settings and mark it as default</p>
   {% endif %}
</div>
{% endif %}
