html:
{% set sektion = frappe.get_doc("Sektion", doc.sektion_id) %}
<div id="logo_kopfzeile"><img src="assets/mvd/img/MV_Logo_F.png"></div>
<div id="anschrift">
    Musterfirma AG<br>
    Maire Mustermensch<br>
    Hans-Mustermensch-Strasse 155b<br>
    8005 Zürich
</div>
<div id="ansprechperson_1">
    {% if not doc.check_ansprechperson %}
        Ansprechperson<br>
        E-Mail<br>
        Direktwahl
    {% endif %}
</div>
<div id="ansprechperson_2">
    {% if not doc.check_ansprechperson %}
        {{ doc.ansprechperson }}<br>
        {{ doc.email_ma }}<br>
        {{ doc.tel_ma }}
    {% endif %}
</div>
<div id="inhalt">
    {{ doc.ort }}, {{ frappe.utils.get_datetime(doc.datum).strftime('%d.%m.%Y') }}<br><br><br>
    <b>{{ doc.brieftitel }}</b><br><br>
    {% if not doc.check_anrede %}
        {{ get_anredekonvention(doc.mv_mitgliedschaft) }}<br>
    {% else %}
        {{ doc.anrede }}<br>
    {% endif %}
    {{ doc.inhalt }}
</div>
<div id="fusszeile_1">
    {{ sektion.fz_links }}
</div>
<div id="fusszeile_2">
    {{ sektion.fz_mitte }}
</div>
<div id="fusszeile_3">
    {{ sektion.fz_rechts }}
</div>
{% if doc.inhalt_2 and doc.inhalt_2 != '<div><br></div>' %}
<div class="page-break"></div>
<div id="seite_2">{{ doc.inhalt_2 }}</div>
{% endif %}
