css:
#mdatenblatt table tr td:first-child + td {
   font-weight:bold;
}
#mdatenblatt table {
    width: 100%;
}

/* all cells */
#mdatenblatt table td, #mdatenblatt table tr, #mdatenblatt table {
margin:0;
padding:0.1em !important;
border:0;
}

/* first and third col */
#mdatenblatt table tr td:first-child,
#mdatenblatt table tr td:first-child+td+td {
    width: 20%;
}

#mdatenblatt table tr td:first-child+td,
#mdatenblatt table tr td:first-child+td+td+td {
    font-weight:bold;
    width: 30%
}

.blbox td {
    font-weight:normal !important;
    border-bottom:1pt solid black !important;
}

