css:
/*
Allgemein
*/
.print-format {
 margin-left: 0mm;
 margin-top: 0mm;
 margin-bottom: 0mm;
 margin-right: 0mm;
}

@font-face {
    font-family: 'MetaOT';
    font-style: normal;
    font-weight: normal;
    src: url('/assets/mvd/font/TWKEverett-Light.woff');
}

#clear_float {
    clear: both;
}

/*
Kopfzeile
*/
#kopfzeile {
    margin-left: 26mm;
    padding-top: 14.75mm;
    min-height: 14.75mm;
    height: 14.75mm;
    max-height: 14.75mm;

    border: 1px solid red;
}

#logo_kopfzeile {
    width: 40mm;
    max-width: 40mm;
    float: left;
}

#ausweis_vorderseite {
    float: right;
}

/*
Anschrift / Referenz
*/
#anschrift {
    margin-left: 26mm;
    margin-top: 28mm;
    width: 117mm;
    float: left;
    font-size: 9.25pt !important;

    border: 1px solid red;
}

#datum {
    margin-top: 28mm;
    float: left;

    border: 1px solid red;
}

#mvd_referenz {
    float: left;
    width: 40mm;

    border: 1px solid red;
}

#mvd_referenz_1 {
    font-size: 6.5pt !important;

    border: 1px solid red;
}

#mvd_referenz_2 {
    font-size: 8.976pt !important;

    border: 1px solid red;
}

/*
Inhalt
*/

#ueberschrift {
    margin-left: 26mm;
    margin-top: 10mm;
    padding-bottom: 11.75mm;

    border: 1px solid red;
}

#inhalt {
    min-height: 150mm;
    margin-left: 26mm;

    border: 1px solid red;
}

/*
Fusszeile / unterer Seitenrand
*/
#unterer_seitenrand {
    min-height: 28.5mm;

    border: 1px solid red;
}
